const sliders=document.querySelectorAll(".slider"),arrows=document.querySelectorAll(".control-arrow"),radioSliders=document.querySelectorAll(".btn-slider");arrows.forEach(function(r){r.addEventListener("click",function(){sliders.forEach(function(r){r.classList.toggle("hidden")}),radioSliders.forEach(function(r){r.classList.toggle("active-radio")})})});
